import React, { useState } from 'react';
import './App.css'

const App=()=>{
    const [list,setList]=useState(0);
   const addItem=()=>{
      console.log("Event occured")
   } 
   let count=0;
   const incItem=()=>{
        // count++;
        console.log("Clicked", count++)
   }
    return(
        <div className="container">
            <div className="todoList">
                <h1>To-Do List</h1>
                <input type="text" placeholder="Enter new items" onChange={addItem} />
                <h2>{list}</h2>
                <button onClick={incItem}><i class="fas fa-plus"></i></button>
                <ol>
                    <li>Rice</li>
                </ol>
            </div>
        </div>
    )
}


export default App;

