import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App=()=>{
  const [num,setNum]=useState();
  const [name,setName]=useState();
  const [img,setImage]=useState([]);
  useEffect(()=>{
    // alert("hello");
    async function getData(){
      const response=await axios.get("https://demo7242716.mockable.io/products");
      // console.log(response);
      let data1=response.data;
      console.log(data1);
      setName(data1.totalCount);
      console.log(data1.totalCount);
      // setImage(response.data.products.landingPageUrl);
      // console.log(response.data.products[0].landingPageUrl);
      // setImage(response.data.products[1].searchImage);
      setImage(data1.products.map((values)=>{
        return values.searchImage;
      }));


    }
    getData();
  })
  return (<>
  <h1>You have choose {num} value</h1>
  <h1>My name is {name}</h1>
  <img src={img} alt="img"></img>
  <select value={num} onChange={(e)=>{
    setNum(e.target.value)
  }}>
    <option value="1">1</option>
    <option value="2">2</option>
    <option value="3">3</option>
    <option value="4">4</option>
  </select>
  </>);
};

export default App;
