import React ,{useState} from 'react';

const App=()=>{
    
    const [data,setData]=useState({
fname:"",
lname:"",
email:"",
phone:"",
    });


    const submitDetails=(e)=>{
        e.preventDefault();
    }

    const inputdata=(e)=>{
// const name=e.target.name;
// console.log(name);
const [name,value]=e.target;
setData((prev)=>{
    if(name==='fname'){
        return{
            fname:value,
            lname:prev.lname,
            email:prev.email,
            phone:prev.phone,
        }
    }else if(name==='lname'){
        return{
        fname:prev.fname,
        lname:value,
        email:prev.email,
        phone:prev.phone,
        }
    }
    else if(name==='email'){
        return{
        fname:prev.fname,
        lname:prev.lname,
        email:value,
        phone:prev.phone,
        }
    }
    else if(name==='email'){
        return{
        fname:prev.fname,
        lname:prev.lname,
        email:prev.email,
        phone:value,
        }
    }
})
    }
return(

    <>
    <form onSubmit={submitDetails}>
    <h1>Hello {data.fname} {data.lname}</h1>
    <h2>{data.email}</h2>
    <p>{data.phone}</p>
<input type="text" placeholder="First name" name="fname" value={data.first} onChange={inputdata}/>
<input type="text" placeholder="last name" name="lname" value={data.last} onChange={inputdata}/>
<input type="email" placeholder="email"  value={data.email} onChange={inputdata} name="email"/>
<input type="text" placeholder="phone"  value={data.phone} onChange={inputdata} name="phone"/>
<button type="submit">Click me</button>
</form>
    </>

)
}
export default App;
