import React,{useState} from 'react';

const App=()=>{
    const [myname,setMyname]=useState("");
    const [email,setemail]=useState("");
    const [password,setpassword]=useState("");
    const [newData,setNewdata]=useState([]);
const submitDetails=(e)=>{
    e.preventDefault();
    if(myname && email && password){

        const newEntries={id:new Date().getTime().toString(),myname:myname,email:email,password:password};
        setNewdata([newEntries]);
        setMyname("");
        setemail("");
        setpassword("");
    }else{
        alert("Please fill the data");
    }
}

return(

<>
<form onSubmit={submitDetails}>
    <div>
        <label htmlFor="name">Name</label>
        <input type="text" placeholder="Enter your name" value={myname} onChange={(e)=>{setMyname(e.target.value)}}/>
    </div>
    <div>
        <label htmlFor="email">
        Email</label>
        <input type="text" placeholder="Enter your email" value={email} onChange={(e)=>{setemail(e.target.value)}}/>
    </div>
    <div>
        <label htmlFor="password">Name</label>
        <input type="password" placeholder="Enter your password" value={password} onChange={(e)=>{setpassword(e.target.value)}} />
    </div>
    <button>Submit</button>
</form>
<div>
    {newData.map((val)=>{
        const {id,myname,email,password}=val;
        return(
            <>
            <div>{id}
<h1>{myname}</h1>
<h1>{email}</h1>
<h1>{password}</h1>
</div>
            </>
        )
    })}
</div>

</>
)
}
export default App;
