import React,{useState} from 'react';

const App=()=>{
    const [fullname,setFullname]=useState({
        fname:"",
        lname:"",
        email:"",
        password:"",
    });
const inputEvent=(e)=>{

const value=e.target.value;
const name=e.target.name;
// console.log(name);
setFullname((prev)=>{
    console.log(prev);
    if(name==='fname'){
        return {
            fname:value,
            lname:prev.lname,
            email:prev.email,
            password:prev.password
        };
    }
    else if(name==='lname'){
return{
            fname:prev.fname,
            lname:value,
            email:prev.email,
            password:prev.password
}
    }
    else if(name==='email'){
        return{
            fname:prev.fname,
            lname:prev.lname,
            email:value,
            password:prev.password
        }
            }
            else if(name==='password'){
                return{
                    fname:prev.fname,
                    lname:prev.lname,
                    email:prev.email,
                    password:value
                }
                    }
});
}
const submitdetails=(e)=>{
    e.preventDefault();
 
}
    return(<>
    <form onSubmit={submitdetails}>
<h1>Hello {fullname.fname} {fullname.lname}</h1>
<h2>{fullname.email}</h2>
<p>{fullname.password}</p>
<input type="text" placeholder="Enter your first name" onChange={inputEvent} name="fname" value={fullname.fname}/>
<input type="text" placeholder="Enter your last name" onChange={inputEvent} name="lname" value={fullname.lname}/>
<input type="text" placeholder="Enter your last email" onChange={inputEvent} name="email" value={fullname.email}/>
<input type="text" placeholder="Enter your last password" onChange={inputEvent} name="password" value={fullname.password}/>
<button type="submit">Submit data</button>
</form>
    </>)
}
export default App;
