   } 
   const newItem=()=>{

   }
  
    return(
        <div className="container">
            <div className="todoList">
                <h1>To-Do List</h1>
                <input type="text" placeholder="Enter new items" onChange={addItem} />
                <button onClick={newItem}><i class="fas fa-plus"></i></button>
                <ol>
                    <li>{list}</li>
                </ol>
            </div>
        </div>
    )
}


