import React,{useState} from 'react';

const App=()=>{
 let time=new Date().toLocaleTimeString();
//  console.log(time);
const [timing,setTiming]=useState(time);

const updateTime=()=>{
    time=new Date().toLocaleTimeString();
    setTiming(time);
}

    return(
        <>
<p>{timing}</p>
<button onClick={updateTime}>Click me</button>

        </>
    )
}
export default App;
