import React,{useState} from 'react';

const App=()=>{
    const [first,setfirst]=useState("");
    const [last,setLast]=useState("");
    const [name,setName]=useState();
    const [namel,setNamel]=useState();
const submitdetails=(e)=>{
    e.preventDefault();
    setName(first);
    setNamel(last)
    setfirst("");
    setLast("");
}
    return(<>
    <form onSubmit={submitdetails}>
<h1>Hello {name}</h1>
<input type="text" placeholder="Enter your first name" onChange={(e)=>{setfirst(e.target.value)}} value={first}/>
<input type="text" placeholder="Enter your last name" onChange={(e)=>{setLast(e.target.value)}} value={last}/>
<button type="submit">Submit data</button>
</form>
    </>)
}
export default App;
