import React ,{useState} from 'react';

const App=()=>{
    const [name,setName]=useState("");
    const [full,setFull]=useState();
    const inputdata=(e)=>{
        setName(e.target.value);
    }
    const submitdata=()=>{
        setFull(name);
        setName("");
    }
return(
    <>
<h1>
    Hello {name}
</h1>
<input type="text" onChange={inputdata} placeholder="Enter your name" />
<button onSubmit={submitdata}> Click me</button>
    </>

)
}
export default App;
