import React,{useState} from 'react';

const App=()=>{
    const [data,setData]=useState("");
    const [name,setName]=useState();
const submitdetails=()=>{
    setName(data);
    setData("");
}
    return(<>
    <form>
<h1>Hello {name}</h1>
<input type="text" placeholder="Enter your name" onChange={(e)=>{setData(e.target.value)}} value={data}/>
<button type="submit">Submit data</button>
</form>
    </>)
}
export default App;
