import React,{useState} from 'react';

const App=()=>{
let color="red";
const [color1,setColor1]=useState(color);
const [text,setText]=useState("Click me")
const changeText=()=>{
    setText("Iam clicked")
}
const clickChange=()=>{
    color="Yaayyyy";
    setText(color);
}
    return(
        <>

<div style={{backgroundColor:color1}}>
    <button onClick={changeText} onDoubleClick={clickChange}>{text}</button>
</div>

        </>
    )
}
export default App;
