import React,{useState} from 'react';
const App=()=>{
    let biodata=[
        {
            id:0,myname:"khanam",age:20
        },
        {
            id:1,myname:"khaiser",age:21
        },{
            id:2,myname:"seeta",age:22
        }
    ]
    const [deleted,setDeleted]=useState(biodata);
    console.log(biodata);
    const deleteAll=(id)=>{
        const newData=deleted.filter((elem)=>{
            return elem.id=id;
        })
        setDeleted(newData);
    }
    return(
        <>
{deleted.map((ele,key)=>{
    return(
        <>
        <h1>{ele.key},{ele.id},{ele.myname},{ele.age}</h1>
        <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Eum, non.</p>
    <button onClick={()=>deleteAll(ele.id)}>Delete</button>
        </>
    )
})}
        </>
    )

}
export default App;
