import React,{useState} from 'react';

const App=()=>{
    const [data,setData]=useState("");
    const [name,setName]=useState();
const submitdetails=()=>{
    setName(data);
    setData("");
}
    return(<>
<h1>Hello {name}</h1>
<input type="text" placeholder="Enter your name" onChange={(e)=>{setData(e.target.value)}} value={data}/>
<button onClick={submitdetails} >Submit data</button>

    </>)
}
export default App;
