import React,{useState} from 'react';

const App=()=>{
    const [myname,setMyname]=useState("");
    const [password,setPassword]=useState("");

    const [data,setData]=useState([]) ;   
    const submitDetails=(e)=>{
        e.preventDefault();
        const time=new Date().getTime().toString();
        const newData={id:time,myname:myname,password:password};
        setData([newData]);
        setMyname("");
        setPassword("");

    }
    return(
        <>
<form onSubmit={submitDetails}>
    <div>
        <label htmlFor="myname">Name</label>
        <input type="text" placeholder="Enter your name" onChange={(e)=>setMyname
        (e.target.value)} value={myname}/>
    </div>
    <div>
        <label htmlFor="password">Password</label>
        <input type="password" placeholder="Enter your password" onChange={(e)=>setPassword
        (e.target.value)} value={password}/>
    </div>
    <button>Submit</button>
</form>

<div>
    {data.map((val)=>{
        return(<>
        <p>{val.myname}</p>
        <p>{val.password}</p>
        </>)
    })}
</div>
        </>
    )
}


export default App;
